public class Solution_9_IsSubsequence {

    public static void main(String... args) {
        System.out.println(isSubsequence("abc", "ahbgdc"));
        System.out.println(isSubsequence("acb", "ahbgdc"));
        System.out.println(isSubsequence("axc", "ahbgdc"));
        System.out.println(isSubsequence("", "ahbgdc"));
        System.out.println(isSubsequence("leeeeetcode", ""));
    }

    public static boolean isSubsequence(String s, String t) {
        if (s.equals("")) return true;
        String[] origin = t.split("");
        String[] temp = s.split("");
        int index = 0;
        for (String value : origin) {
            if (index == s.length()) break;
            if (value.equals(temp[index])) {
                index++;
            }
        }
        return index == s.length();
    }
}